import React, { useEffect, useState } from "react";
import "./FullCard.css";
import axios from "C:/Users/Ronak/Desktop/Coding/HotelBook/hotelbooking-react/hotel-booking/src/axios.jsx";

const FullCard = () => {
  const [myData, setMyData] = useState([]);
  const [isError, setIsError] = useState("");

  const getMyPostData = async () => {
    try {
      const res = await axios.get("api/hotels/list/");
      setMyData(res.data);
    } catch (error) {
      setIsError(error.message);
    }
  };

  useEffect(() => {
    getMyPostData();
  }, []);

  return (
    <>
      {isError !== "" && <h2>{isError}</h2>}

      {myData.map((post) => {
        const { name, hotel_id, city, stars,average_price } = post;
        return (
          <div className="fullHotelCard" key={hotel_id}>
            <div className="fullHotelImg">
              <div className="mainHotelImg">
                <img
                  src="data:image/jpeg;base64,"
                  alt="Room img"
                />
              </div>
              <span className="secHotelImg">
                <img
                  src="data:image/jpeg;base64,"
                  alt="more room img"
                />
              </span>
              <span className="secHotelImg">
                <img
                  src="data:image/jpeg;base64,"
                  alt="more room img"
                />
              </span>
              <span className="secHotelImg" id="viewmore">
                <img
                  src="data:image/jpeg;base64,"
                  alt="more room img"
                />
                <div className="textOnImage">View More</div>
              </span>
            </div>
            <div className="hotelCardText">
              <div className="hotelCardName">{name}</div>
              <div className="hotelCardLoc">{city}</div>
              <div className="hotelCardhigh">
                Highlights
                <li>ABC</li>
                <li>ABC</li>
                <li>ABC</li>
              </div>
            </div>
            <div className="hotelCardStars">
              {Array.from({ length: stars }).map((_, i) => (
                <i key={i} className="fa fa-star noStars"></i>
              ))}
            </div>
            <div className="halfVerticalBar"></div>
            <div className="hotelCardBook">
              <div className="hotelCardRating">Rating</div>
              <div className="hotelCardRev">
                <span>{stars}</span> Very Good
              </div>
              <div className="hotelCardRevNo">(334 Rating)</div>
              <div className="hotelCardPrice">{average_price}</div>
              <div className="hotelCardBtn">
                <button type="Submit">Book Now</button>
              </div>
            </div>
          </div>
        );
      })}
    </>
  );
};
export default FullCard;
